mapper.update:
UPDATE auction
SET state = 'unprocessing'
WHERE endDate <=
#{currentDate}
AND state = 'processing';

UPDATE bid
SET bidState = 2
WHERE auctionNum IN (
SELECT auctionNum FROM
auction
WHERE endDate <=
#{currentDate}
AND state = 'unprocessing'
);

UPDATE bid
SET bidState = 2
WHERE auctionNum = #{auctionNum}
AND bidNum = (
SELECT bidNum
FROM (SELECT bidNum FROM bid WHERE auctionNum = #{auctionNum} ORDER BY
bidPrice DESC LIMIT 1) AS highestBid
);

UPDATE bid
SET bidState = 0
WHERE auctionNum = #{auctionNum}
AND bidNum != (
SELECT bidNum
FROM (SELECT bidNum FROM bid WHERE auctionNum = #{auctionNum} ORDER BY
bidPrice DESC LIMIT 1) AS highestBid
);

mapper.select:
SELECT auctionNum
FROM auction
WHERE endDate <=
#{currentDate}
AND state = 'unprocessing';

SELECT COUNT(*)
FROM transaction
WHERE auctionNum = #{auctionNum}

SELECT a.auctionNum, a.id, a.title, a.startDate, a.endDate, a.state, a.startPrice, a.minSalary, a.maxSalary, a.bidMaxPrice, a.Occupation, a.period, a.education, a.employmentType, a.stack, a.Certification, a.location, a.portfolio, p.nickName, p.email, p.password, p.address, p.sex, p.age, p.personImage, p.status FROM auction a JOIN person p ON a.id = p.id WHERE 1=1
AND a.location IN
#{location}
AND a.stack IN
#{stack}
AND a.occupation IN
#{Occupation}
AND a.period IN
#{period}
AND a.education IN
#{education}
AND a.certification IN
#{Certification}
AND a.employmentType IN
#{employmentType}
ORDER BY a.auctionNum LIMIT #{offset}, #{pageSize}

select a.*,
p.nickname,p.email,p.address,p.sex,p.age,p.personimage,p.status
from
auction as a inner join person as p on a.id = p.id


		and occupation like #{occupation}


		and period like #{period}


		and education like #{education}


		and employmentType like #{employmentType}


		and stack like #{stack}


		and certification like #{certification}


		and location like #{location}

	and a.state = 'processing'

order by a.auctionNum
limit 9 offset #{offset}

mapper.update:
update auction set
bidMaxPrice=#{bidMaxPrice}
where auctionNum=#{auctionNum}
