mapper.select:
SELECT t.*, a.*, p.personImage FROM transaction t JOIN auction a ON t.auctionNum = a.auctionNum JOIN person p ON t.sellerId = p.id WHERE t.buyerId=#{buyerId}
and STR_TO_DATE(t.date, '%Y-%m-%d') between STR_TO_DATE(#{filterDate}, '%Y-%m-%d') and STR_TO_DATE(now(), '%Y-%m-%d')
and STR_TO_DATE(t.date, '%Y-%m-%d') between STR_TO_DATE(#{startDate}, '%Y-%m-%d') and STR_TO_DATE(#{endDate}, '%Y-%m-%d')
order by t.date desc limit #{limit} offset #{offset};

SELECT t.*, a.*, p.personImage FROM transaction t JOIN auction a ON t.auctionNum = a.auctionNum JOIN person p ON t.sellerId = p.id WHERE t.sellerId=#{sellerId}
and STR_TO_DATE(t.date, '%Y-%m-%d') between STR_TO_DATE(#{filterDate}, '%Y-%m-%d') and STR_TO_DATE(now(), '%Y-%m-%d')
and STR_TO_DATE(t.date, '%Y-%m-%d') between STR_TO_DATE(#{startDate}, '%Y-%m-%d') and STR_TO_DATE(#{endDate}, '%Y-%m-%d')
order by t.date desc limit #{limit} offset #{offset};


        SELECT * 
		FROM Company 
		WHERE id = (SELECT buyerId FROM transaction WHERE auctionNum = #{auctionNum});
